import axios from "axios";
import { useState, useEffect } from "react";
import "./app.css";
/*this object will have your keys and the base urs*/
/* please get your key from openweathermap.org*/
const api = {
  key: "69dfae742d38c6fd4cdf34760b0e3b91",
  base: "https://api.openweathermap.org/data/2.5/",
};

function App() {
  const cities = ["Select a City", "Bishkek", "california", "Sonsonate"];

  const [selectedCity, setSelectedCity] = useState(cities[1]);
  const [weather, setWeather] = useState("");

  useEffect(() => {
    const data = callToApi();
    console.log(data);
  }, [selectedCity]);

  const callToApi = async () => {
    const resq = await axios.get(
      `${api.base}weather?q=${selectedCity}&units=metric&APPID=${api.key}`
    );
    return resq.data;
  };
  const onClickHandler = () => {
    if (selectedCity !== "")
      fetch(`${api.base}weather?q=${selectedCity}&units=metric&APPID=${api.key}`)
        .then((res) => res.json())
        .then((result) => {
          setWeather(result);
          setSelectedCity("");
          console.log(result);
        });
  };

  const tempCheck = () => {
    if (weather !== "") {
      let temp = Math.round(weather.main.temp);
      let finalText = temp + "Â°";
      return finalText;
    }
  };

  const situationCheck = () => {
    if (weather !== "") {
      let situation = weather.weather[0].main;
      return (
        <div className="situation">
          <h3>{situation}</h3>
        </div>
      );
    }
  };
  const locationCheck = () => {
    if (weather !== "") {
      let location = weather.name.split(" ").slice(0, 1);
      return location;
    }
  };

  return (
    <div
      className={
        typeof weather.main != "undefined"
          ? weather.main.temp > 12
            ? "container warm"
            : "container"
          : "container"
      }
    >
      <div
        className={
          typeof weather.main != "undefined" ? (weather.main.temp > 12 ? "app warm" : "app") : "app"
        }
      >
        <main>
          <div className="top">
            <div className="location">{locationCheck()}</div>
            {/* render The city*/}
            <div>
              <div className="temp">
                <h2>{tempCheck()}</h2> {/*render the temperature*/}
              </div>
              {situationCheck()} {/*render Situation*/}
            </div>
          </div>
          <div className="select-area">
            {" "}
            {/*create a selector to show the cities*/}
            <select
              className="custom-select"
              value={selectedCity}
              onChange={(e) => {
                setSelectedCity(e.target.value);
              }}
            >
              {cities.map((el, i) => {
                return (
                  <option key={i} value={cities[i]}>
                    {cities[i]}
                  </option>
                );
              })}
            </select>
            <br />
            <button className="btn" onClick={onClickHandler}>
              Submit
            </button>
          </div>
        </main>
      </div>
    </div>
  );
}

export default App;
